var $bm_rt;
var x, y;
try {
    function smartAnchor(n) {
        var xAnchorBasedOnWidth, yAnchorBasedOnHeight, xAnchorBasedOnCustom, yAnchorBasedOnCustom, sourceRectWidth, sourceRectHeight, layerWidth, layerHeight, xAnchor, yAnchor, leftSide, middleXSide, rightSide, topSide, middleYSide, bottomSide;
        xAnchorBasedOnWidth = effect('Smart Anchor')('X Anchor Based On').value;
        yAnchorBasedOnHeight = effect('Smart Anchor')('Y Anchor Based On').value;
        xAnchorBasedOnCustom = effect('Smart Anchor')('X Anchor - Custom Time').value;
        yAnchorBasedOnCustom = effect('Smart Anchor')('Y Anchor - Custom Time').value;
        sourceRectWidth = thisLayer.sourceRectAtTime(sourceTime([
            time,
            inPoint,
            outPoint - thisComp.frameDuration,
            inPoint + (outPoint - inPoint) / 2 - thisComp.frameDuration / 2,
            xAnchorBasedOnCustom
        ][$bm_sub(xAnchorBasedOnWidth, 1)]));
        sourceRectHeight = thisLayer.sourceRectAtTime(sourceTime([
            time,
            inPoint,
            outPoint - thisComp.frameDuration,
            inPoint + (outPoint - inPoint) / 2 - thisComp.frameDuration / 2,
            yAnchorBasedOnCustom
        ][$bm_sub(yAnchorBasedOnHeight, 1)]));
        layerWidth = sourceRectWidth.width;
        layerHeight = sourceRectHeight.height;
        xAnchor = effect('Smart Anchor')('X Anchor Point').value;
        yAnchor = effect('Smart Anchor')('Y Anchor Point').value;
        leftSide = sourceRectWidth.left;
        middleXSide = $bm_sum(leftSide, $bm_div(layerWidth, 2));
        rightSide = $bm_sum(leftSide, layerWidth);
        topSide = sourceRectHeight.top;
        middleYSide = $bm_sum(topSide, $bm_div(sourceRectWidth.height, 2));
        bottomSide = $bm_sum(topSide, sourceRectHeight.height);
        return [
            leftSide,
            middleXSide,
            rightSide,
            topSide,
            middleYSide,
            bottomSide
        ][n];
    }
    ;
    x = [
        smartAnchor(0),
        smartAnchor(1),
        smartAnchor(2),
        value[0]
    ][$bm_sub(xAnchor, 1)];
    y = [
        smartAnchor(3),
        smartAnchor(4),
        smartAnchor(5),
        value[1]
    ][$bm_sub(yAnchor, 1)];
    $bm_rt = [
        x,
        y
    ];
} catch (err) {
    $bm_rt = value;
}
;
